(430, 1, '2025-02-04 23:36:31', '2025-02-04 22:36:31', '<!-- wp:group {\"backgroundColor\":\"custom-brown\",\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\",\"justifyContent\":\"space-between\"}} -->\n<div class=\"wp-block-group has-custom-brown-background-color has-background\"><!-- wp:site-logo {\"width\":96,\"shouldSyncIcon\":true,\"className\":\"is-style-rounded\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|10\",\"bottom\":\"var:preset|spacing|10\",\"left\":\"var:preset|spacing|10\",\"right\":\"var:preset|spacing|10\"}},\"layout\":{\"selfStretch\":\"fit\",\"flexSize\":null}}} /-->\n\n<!-- wp:search {\"label\":\"\",\"showLabel\":false,\"placeholder\":\"Rechercher...\",\"buttonText\":\"Rechercher\",\"buttonUseIcon\":true,\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|10\",\"bottom\":\"var:preset|spacing|10\",\"left\":\"var:preset|spacing|10\",\"right\":\"var:preset|spacing|10\"}}}} /-->\n\n<!-- wp:group {\"className\":\"Account-link\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|10\",\"bottom\":\"var:preset|spacing|10\",\"left\":\"var:preset|spacing|10\",\"right\":\"var:preset|spacing|10\"}}},\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group Account-link\" style=\"padding-top:var(--wp--preset--spacing--10);padding-right:var(--wp--preset--spacing--10);padding-bottom:var(--wp--preset--spacing--10);padding-left:var(--wp--preset--spacing--10)\"><!-- wp:shortcode -->\n[lien_connexion_compte]\n<!-- /wp:shortcode --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"0\",\"bottom\":\"0\"},\"padding\":{\"top\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"right\":\"0\"}}},\"backgroundColor\":\"custom-red\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group has-custom-red-background-color has-background\" style=\"margin-top:0;margin-bottom:0;padding-top:0;padding-right:0;padding-bottom:0;padding-left:0\"><!-- wp:group {\"align\":\"wide\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"0\",\"bottom\":\"0\"}},\"dimensions\":{\"minHeight\":\"52px\"}},\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-group alignwide\" style=\"min-height:52px;padding-top:0;padding-bottom:0\"><!-- wp:navigation {\"ref\":5,\"textColor\":\"custom-light-pink\",\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\",\"flexWrap\":\"wrap\"}} /--></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:html -->\n<!-- Sidebar -->\n<div id=\"sidebar\">\n    <button id=\"closeSidebar\" class=\"sidebar-close\">&times;</button>\n    <div class=\"sidebar-title\">\n        <span>Catégories</span>\n    </div>\n    <nav>\n        <ul>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/concepts\" class=\"NavlinkConcepts\">Concepts</a></li>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/corps\" class=\"NavlinkCorps\">Corps</a></li>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/figures\" class=\"NavlinkFigures\">Figures</a></li>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/institutions\" class=\"NavlinkInstitutions\">Institutions</a></li>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/lieux\" class=\"NavlinkLieux\">Lieux</a></li>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/pratiques\" class=\"NavlinkPratiques\">Pratiques</a></li>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/relations\" class=\"NavlinkRelations\">Relations</a></li>\n            <li><a href=\"http://localhost/DicoTrav/index.php/category/temps\" class=\"NavlinkTemps\">Temps</a></li>\n        </ul>\n    </nav>\n</div>\n\n<!-- Overlay pour le mobile -->\n<div id=\"sidebarOverlay\"></div>\n\n<!-- Bouton pour ouvrir la sidebar (flèche au lieu du burger) -->\n<button id=\"openSidebar\" class=\"sidebar-toggle\">&#8594;</button>\n\n<script>\ndocument.addEventListener(\"DOMContentLoaded\", function() {\n    var openBtn = document.getElementById(\"openSidebar\");\n    var closeBtn = document.getElementById(\"closeSidebar\");\n    var sidebar = document.getElementById(\"sidebar\");\n    var overlay = document.getElementById(\"sidebarOverlay\");\n    var body = document.body;\n\n    function toggleSidebar() {\n        body.classList.toggle(\"sidebar-open\");\n    }\n\n    openBtn.addEventListener(\"click\", toggleSidebar);\n    closeBtn.addEventListener(\"click\", toggleSidebar);\n    overlay.addEventListener(\"click\", toggleSidebar);\n});\n</script>\n\n<style>\n    body.sidebar-open #sidebar {\n        transform: translateX(0);\n        transition: transform 0.5s ease, opacity 0.5s ease-in-out; /* Transition fluide */\n        opacity: 1;\n    }\n\n    body.sidebar-open #sidebarOverlay {\n        display: block;\n        opacity: 0.5;\n        transition: opacity 0.5s ease-in-out;\n    }\n\n    #sidebar {\n        position: fixed;\n        top: 0;\n        left: 0;\n        height: 100%;\n        min-width: 250px;\n        background: #6A4C39;\n        color: #EDE0D4;\n        transform: translateX(-100%);\n        transition: transform 0.3s ease-in-out;\n        padding: 40px;\n        z-index: 200;\n        opacity: 0;\n    }\n\n    #sidebar .sidebar-title {\n        margin-bottom: 20px;\n        font-size: 1.5rem;\n        color: #EDE0D4;\n        text-align: center;\n    }\n\n    #sidebar nav ul {\n        list-style: none;\n        padding: 0;\n        margin: 0;\n    }\n\n    #sidebar nav ul li {\n        margin: 1rem 0;\n    }\n\n    #sidebar nav ul li a {\n        color: #EDE0D4;\n        text-decoration: none;\n        display: block;\n        padding: 0.8rem 1rem;\n        font-size: 1.1rem;\n        background: #8D3C41;\n        text-align: center;\n        border-radius: 5px;\n        transition: background-color 0.3s ease, transform 0.2s ease;\n    }\n\n    #sidebar nav ul li a:hover {\n        transform: translateX(5px); /* Mouvement léger vers la droite */\n    }\n\n    /* Couleurs pour chaque catégorie lors du hover */\n    .NavlinkConcepts:hover { background-color: #A899C6 !important; }\n    .NavlinkCorps:hover { background-color: #D7C4A1 !important; }\n    .NavlinkFigures:hover { background-color: #E8D4A2 !important; }\n    .NavlinkInstitutions:hover { background-color: #6C8FA8 !important; }\n    .NavlinkLieux:hover { background-color: #9BBF9F !important; }\n    .NavlinkPratiques:hover { background-color: #E9B97E !important; }\n    .NavlinkRelations:hover { background-color: #A8766E !important; }\n    .NavlinkTemps:hover { background-color: #B2B2B2 !important; }\n\n    #sidebarOverlay {\n        display: none;\n        position: fixed;\n        top: 0;\n        left: 0;\n        min-width: 100vw;\n        min-height: 100vh;\n        background: rgba(0, 0, 0, 0.5);\n        z-index: 100;\n        opacity: 0;\n        margin: 0;\n    }\n\n    /* Bouton pour ouvrir la sidebar (flèche centrée à gauche) */\n    .sidebar-toggle {\n        position: fixed;\n        top: 50%;\n        left: 12px;\n        transform: translateX(-2px);\n        z-index: 101;\n        background: #6A4C39;\n        color: #EDE0D4;\n        border: none;\n        padding: 10px 15px;\n        font-size: 1.5rem;\n        cursor: pointer;\n        transition: transform 0.3s ease;\n        border-radius: 5px;\n    }\n\n    .sidebar-toggle:hover {\n        transform: translateX(2px); /* Animation légère */\n    }\n\n    /* Bouton de fermeture (croix) */\n    .sidebar-close {\n        position: absolute;\n        top: 10px;\n        right: 10px;\n        background: none;\n        border: none;\n        color: #EDE0D4;\n        font-size: 1.5rem;\n        cursor: pointer;\n        transition: transform 0.3s ease, scale 0.2s ease;\n    }\n\n    /* Hover sur la croix (enlève la rotation) */\n    .sidebar-close:hover {\n        transform: scale(1.2);\n        color: #FF0000; /* Couleur rouge de la croix */\n    }\n</style>\n<!-- /wp:html --></div>\n<!-- /wp:group -->', 'Header', '', 'publish', 'closed', 'closed', '', 'header', '', '', '2025-02-09 20:31:59', '2025-02-09 19:31:59', '', 0, 'http://localhost/DicoTrav/index.php/header/', 0, 'wp_template_part', '', 0),
