   }\n\n    /* Points de la timeline */\n    .timeline-point {\n        width: 20px;\n        height: 20px;\n        background-color: #0073aa;\n        border-radius: 50%;\n        position: absolute;\n        left: 50%;\n        transform: translateX(-50%);\n        transition: transform 0.3s ease-in-out;\n    }\n\n    /* Contenu des périodes */\n    .timeline-content {\n        background: #f9f9f9;\n        font-size: 14px;\n        padding: 15px;\n        border-radius: 5px;\n        width: 40%;\n        text-align: center;\n        position: relative;\n        left: 50%;\n        transform: translateX(-50%);\n        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);\n        transition: all 0.3s ease-in-out;\n    }\n\n    /* Effet d\'animation quand on survole */\n    .timeline-item:hover .timeline-point {\n        transform: scale(1.3) translateX(-50%);\n    }\n</style>\n\n<div class=\"timeline\">\n    <div class=\"timeline-item\">\n        <div class=\"timeline-point\"></div>\n        <div class=\"timeline-content\">\n            <h3>Moyen Âge</h3>\n            <p>476 - 1492</p>\n        </div>\n    </div>\n    <div class=\"timeline-item\">\n        <div class=\"timeline-point\"></div>\n        <div class=\"timeline-content\">\n            <h3>Époque Moderne</h3>\n            <p>1492 - 1789</p>\n        </div>\n    </div>\n    <div class=\"timeline-item\">\n        <div class=\"timeline-point\"></div>\n        <div class=\"timeline-content\">\n            <h3>Époque Contemporaine</h3>\n            <p>1789 - Aujourd\'hui</p>\n        </div>\n    </div>\n</div>\n\n<script>\n    // Animation au scroll : effet d\'apparition\n    document.addEventListener(\"DOMContentLoaded\", function () {\n        const timelineItems = document.querySelectorAll(\".timeline-item\");\n\n        const observer = new IntersectionObserver(entries => {\n            entries.forEach(entry => {\n                if (entry.isIntersecting) {\n                    entry.target.classList.add(\"visible\");\n                }\n            });\n        }, { threshold: 0.5 });\n\n        timelineItems.forEach(item => {\n            observer.observe(item);\n        });\n    });\n</script>\n\n<!-- /wp:html -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Timeline</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'Loop', '', 'inherit', 'closed', 'closed', '', '459-revision-v1', '', '', '2025-02-19 16:30:32', '2025-02-19 15:30:32', '', 459, 'http://localhost/DicoTrav/?p=657', 0, 'revision', '', 0);
