debar (flche au lieu du burger) -->\n<button id=\"openSidebar\" class=\"sidebar-toggle\">&#8594;</button>\n\n<script>\ndocument.addEventListener(\"DOMContentLoaded\", function() {\n    var openBtn = document.getElementById(\"openSidebar\");\n    var closeBtn = document.getElementById(\"closeSidebar\");\n    var sidebar = document.getElementById(\"sidebar\");\n    var overlay = document.getElementById(\"sidebarOverlay\");\n    var body = document.body;\n\n    function toggleSidebar() {\n        body.classList.toggle(\"sidebar-open\");\n    }\n\n    openBtn.addEventListener(\"click\", toggleSidebar);\n    closeBtn.addEventListener(\"click\", toggleSidebar);\n    overlay.addEventListener(\"click\", toggleSidebar);\n});\n</script>\n\n<style>\n    body.sidebar-open #sidebar {\n        transform: translateX(0);\n        transition: transform 0.5s ease, opacity 0.5s ease-in-out; /* Transition fluide */\n        opacity: 1;\n    }\n\n    body.sidebar-open #sidebarOverlay {\n        display: block;\n        opacity: 0.5;\n        transition: opacity 0.5s ease-in-out;\n    }\n\n    #sidebar {\n        position: fixed;\n        top: 0;\n        left: 0;\n        height: 100%;\n        min-width: 250px;\n        background: #6A4C39;\n        color: #EDE0D4;\n        transform: translateX(-100%);\n        transition: transform 0.3s ease-in-out;\n        padding: 40px;\n        z-index: 200;\n        opacity: 0;\n    }\n\n    #sidebar .sidebar-title {\n        margin-bottom: 20px;\n        font-size: 1.5rem;\n        color: #EDE0D4;\n        text-align: center;\n    }\n\n    #sidebar nav ul {\n        list-style: none;\n        padding: 0;\n        margin: 0;\n    }\n\n    #sidebar nav ul li {\n        margin: 1rem 0;\n    }\n\n    #sidebar nav ul li a {\n        color: #EDE0D4;\n        text-decoration: none;\n        display: block;\n        padding: 0.8rem 1rem;\n        font-size: 1.1rem;\n        background: #8D3C41;\n        text-align: center;\n        border-radius: 5px;\n        transition: background-color 0.3s ease, transform 0.2s ease;\n    }\n\n    #sidebar nav ul li a:hover {\n        transform: translateX(5px); /* Mouvement lger vers la droite */\n    }\n\n    /* Couleurs pour chaque catgorie lors du hover */\n    .NavlinkConcepts:hover { background-color: #A899C6 !important; }\n    .NavlinkCorps:hover { background-color: #D7C4A1 !important; }\n    .NavlinkFigures:hover { background-color: #E8D4A2 !important; }\n    .NavlinkInstitutions:hover { background-color: #6C8FA8 !important; }\n    .NavlinkLieux:hover { background-color: #9BBF9F !important; }\n    .NavlinkPratiques:hover { background-color: #E9B97E !important; }\n    .NavlinkRelations:hover { background-color: #A8766E !important; }\n    .NavlinkTemps:hover { background-color: #B2B2B2 !important; }\n\n    #sidebarOverlay {\n        display: none;\n        position: fixed;\n        top: 0;\n        left: 0;\n        min-width: 100vw;\n        min-height: 100vh;\n        background: rgba(0, 0, 0, 0.5);\n        z-index: 100;\n        opacity: 0;\n        margin: 0;\n    }\n\n    /* Bouton pour ouvrir la sidebar (flche centre  gauche) */\n    .sidebar-toggle {\n        position: fixed;\n        top: 50%;\n        left: 12px;\n        transform: translateX(-2px);\n        z-index: 101;\n        background: #6A4C39;\n        color: #EDE0D4;\n        border: none;\n        padding: 10px 15px;\n        font-size: 1.5rem;\n        cursor: pointer;\n        transition: transform 0.3s ease;\n        border-radius: 5px;\n    }\n\n    .sidebar-toggle:hover {\n        transform: translateX(2px); /* Animation lgre */\n    }\n\n    /* Bouton de fermeture (croix) */\n    .sidebar-close {\n        position: absolute;\n        top: 10px;\n        right: 10px;\n        background: none;\n        border: none;\n        color: #EDE0D4;\n        font-size: 1.5rem;\n        cursor: pointer;\n        transition: transform 0.3s ease, scale 0.2s ease;\n    }\n\n    /* Hover sur la croix (enlve la rotation) */\n    .sidebar-close:hover {\n        transform: scale(1.2);\n        color: #FF0000; /* Couleur rouge de la croix */\n    }\n</style>\n<!-- /wp:html --></div>\n<!-- /wp:group -->', 'Header', '', 'publish', 'closed', 'closed', '', 'header', '', '', '2025-02-09 20:31:59', '2025-02-09 19:31:59', '', 0, 'http://localhost/DicoTrav/index.php/header/', 0, 'wp_template_part', '', 0),
