(657, 1, '2025-02-19 16:30:32', '2025-02-19 15:30:32', '<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:spacer {\"height\":\"53px\"} -->\n<div style=\"height:53px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Dernires publications</h2>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":\"55px\"} -->\n<div style=\"height:55px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:latest-posts {\"postLayout\":\"grid\"} /--></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Atlas</h2>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"id\":646,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/DicoTrav/wp-content/uploads/2025/02/Western_Europe_map.svg\" alt=\"\" class=\"wp-image-646\"/><figcaption class=\"wp-element-caption\">Atlas...</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:html -->\n<style>\n    /* Timeline Container */\n    .timeline {\n        display: flex;\n        flex-direction: column;\n        align-items: center;\n        position: relative;\n        width: 100%;\n        max-width: 400px;\n        margin: auto;\n        padding: 20px;\n    }\n\n    /* Ligne verticale */\n    .timeline::before {\n        content: \"\";\n        position: absolute;\n        width: 4px;\n        background-color: #0073aa;\n        top: 0;\n        bottom: 0;\n        left: 50%;\n        transform: translateX(-50%);\n    }\n\n    /* lments de la timeline */\n    .timeline-item {\n        display: flex;\n        align-items: center;\n        margin: 20px 0;\n        position: relative;\n        width: 100%;\n    }\n\n    /* Points de la timeline */\n    .timeline-point {\n        width: 20px;\n        height: 20px;\n        background-color: #0073aa;\n        border-radius: 50%;\n        position: absolute;\n        left: 50%;\n        transform: translateX(-50%);\n        transition: transform 0.3s ease-in-out;\n    }\n\n    /* Contenu des priodes */\n    .timeline-content {\n        background: #f9f9f9;\n        font-size: 14px;\n        padding: 15px;\n        border-radius: 5px;\n        width: 40%;\n        text-align: center;\n        position: relative;\n        left: 50%;\n        transform: translateX(-50%);\n        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);\n        transition: all 0.3s ease-in-out;\n    }\n\n    /* Effet d\'animation quand on survole */\n    .timeline-item:hover .timeline-point {\n        transform: scale(1.3) translateX(-50%);\n    }\n</style>\n\n<div class=\"timeline\">\n    <div class=\"timeline-item\">\n        <div class=\"timeline-point\"></div>\n        <div class=\"timeline-content\">\n            <h3>Moyen ge</h3>\n            <p>476 - 1492</p>\n        </div>\n    </div>\n    <div class=\"timeline-item\">\n        <div class=\"timeline-point\"></div>\n        <div class=\"timeline-content\">\n            <h3>poque Moderne</h3>\n            <p>1492 - 1789</p>\n        </div>\n    </div>\n    <div class=\"timeline-item\">\n        <div class=\"timeline-point\"></div>\n        <div class=\"timeline-content\">\n            <h3>poque Contemporaine</h3>\n            <p>1789 - Aujourd\'hui</p>\n        </div>\n    </div>\n</div>\n\n<script>\n    // Animation au scroll : effet d\'apparition\n    document.addEventListener(\"DOMContentLoaded\", function () {\n        const timelineItems = document.querySelectorAll(\".timeline-item\");\n\n        const observer = new IntersectionObserver(entries => {\n            entries.forEach(entry => {\n                if (entry.isIntersecting) {\n                    entry.target.classList.add(\"visible\");\n                }\n            });\n        }, { threshold: 0.5 });\n\n        timelineItems.forEach(item => {\n            observer.observe(item);\n        });\n    });\n</script>\n\n<!-- /wp:html -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Timeline</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'Loop', '', 'inherit', 'closed', 'closed', '', '459-revision-v1', '', '', '2025-02-19 16:30:32', '2025-02-19 15:30:32', '', 459, 'http://localhost/DicoTrav/?p=657', 0, 'revision', '', 0);
